package com.yusufaytas.leetcode;

import java.util.HashMap;
import java.util.HashSet;
import java.util.Map;
import java.util.Set;

/*
Given a string s and an integer k, return the length of the longest substring of
s such that the frequency of each character in this substring is greater than or equal to k.

Example 1:

Input: s = "aaabb", k = 3
Output: 3
Explanation: The longest substring is "aaa", as 'a' is repeated 3 times.

Example 2:

Input: s = "ababbc", k = 2
Output: 5
Explanation: The longest substring is "ababb", as 'a' is repeated 2 times and 'b' is repeated 3 times.

 */
public class LongestSubstringKRepeatingCharacters
{
    public int longestSubstring(final String s, final int k)
    {
        int max = 0;
        for (int i = 0; i < s.length(); i++)
        {
            final Map<Character, Integer> charCounts = new HashMap<>();
            final Set<Character> completed = new HashSet<>();
            for (int j = i; j < s.length(); j++)
            {
                if (!completed.contains(s.charAt(j)))
                {
                    if (charCounts.getOrDefault(s.charAt(j), 0) == k - 1)
                    {
                        charCounts.remove(s.charAt(j));
                        completed.add(s.charAt(j));
                    }
                    else
                    {
                        charCounts.put(s.charAt(j), charCounts.getOrDefault(s.charAt(j), 0) + 1);
                    }
                }
                if (max < j - i + 1 && charCounts.isEmpty())
                {
                    max = j - i + 1;
                }
            }
        }
        return max;
    }

    public static void main(String[] args)
    {
        final String s = "";
        System.out.println(new LongestSubstringKRepeatingCharacters().longestSubstring(s, 301));
    }
}
